CREATE FUNCTION `firstpy`(words varchar(255)) RETURNS varchar(255) CHARSET utf8
begin
	declare firstletter char(1);
	declare firstlettergbk char(1);
	declare letterhex varchar(10);

	set @firstletter = upper(left(words,1));
	set @firstlettergbk = convert(@firstletter using gbk);
	set @letterhex = hex(@firstletter);

	if @letterhex >= "8140" and @letterhex <="FEA0" then
		begin
			select pinyin into @firstletter from hzpy where hanzi >= @firstlettergbk limit 1;
		end;
	end if;
	return @firstletter;
end;;
